mapper.select:
	select

	from patient_blood_pressure_auto
	where 1=1

		AND bp_deviceID = #{bp_deviceID,jdbcType=VARCHAR}


		AND patient_phone = #{patientPhone,jdbcType=VARCHAR}


		AND device_from_type = #{deviceFromType,jdbcType=INTEGER}


        AND bp_measuretime = #{bp_measuretime,jdbcType=TIMESTAMP}
