mapper.select:
select

from health_device_info
where 1=1

	AND device_ID = #{deviceId,jdbcType=VARCHAR}


	AND device_role = #{deviceRole,jdbcType=INTEGER}


	AND device_type = #{deviceType,jdbcType=INTEGER}


	AND device_from = #{deviceFrom,jdbcType=INTEGER}


	AND patient_phone = #{patientPhone,jdbcType=VARCHAR}

mapper.insert:

insert into health_device_info (id, device_ID, device_name, 
  device_role, device_type, device_from, 
  patient_phone, createtime, updatetime
  )
values (#{id,jdbcType=INTEGER}, #{deviceId,jdbcType=VARCHAR}, #{deviceName,jdbcType=VARCHAR}, 
  #{deviceRole,jdbcType=INTEGER}, #{deviceType,jdbcType=INTEGER}, #{deviceFrom,jdbcType=INTEGER}, 
  #{patientPhone,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}
  )
