mapper.select:
select 

from message_reminding
where message_status=0 and patient_phone = #{patientPhone,jdbcType=VARCHAR}

select

from message_reminding
where 1=1

	AND patient_phone = #{patientPhone,jdbcType=VARCHAR}


	AND message_type = #{messageType,jdbcType=INTEGER}


	AND message_status = #{messageStatus,jdbcType=INTEGER}


	AND message_ext like CONCAT(CONCAT('%',#{doctorPhone}),'%')
